import { createSlice } from '@reduxjs/toolkit';
import { setLoaderProgress } from './LoaderValueSlice'

const homeNewsSlice = createSlice({
    name: 'homeNewsSlice',
    initialState: [],
    reducers: {
        setHomeNews(state, actions) {
            return actions.payload;
        }
    }
})

export function fetchNews() {
    return async function fetchingProducts(dispatch, getState) {
        dispatch(setLoaderProgress(30))
        try {
            const res = await fetch('https://newsapi.org/v2/top-headlines?apiKey=2af91b532974457caf3e61b21463a000&country=in&pageSize=6&page=1&category=general');
            const data = await res.json();
            console.log(data);
            dispatch(setHomeNews(data.articles));
            dispatch(setLoaderProgress(100))
        } catch (error) {
            console.log(error);
        }
    }
}

export const { setHomeNews } = homeNewsSlice.actions;
export default homeNewsSlice.reducer;
