import { createSlice } from '@reduxjs/toolkit';

const loaderValueSlice = createSlice({
    name: 'loaderValueSlice',
    initialState: 0,
    reducers: {
        setLoaderProgress(state, actions) {
            return actions.payload;
        }
    }
})

export const { setLoaderProgress } = loaderValueSlice.actions;
export default loaderValueSlice.reducer;
