import { createSlice } from '@reduxjs/toolkit';
import { setLoaderProgress } from './LoaderValueSlice'

const mainNewsSlice = createSlice({
    name: 'mainNewsSlice',
    initialState: [],
    reducers: {
        setMainNews(state, actions) {
            return actions.payload;
        }
    }
})

export function fetchNews(category) {
    return async function fetchingProducts(dispatch, getState) {
        dispatch(setLoaderProgress(30))
        try {
            const res = await fetch(`https://newsapi.org/v2/top-headlines?apiKey=2af91b532974457caf3e61b21463a000&country=in&category=${category}`);
            const data = await res.json();
            console.log(data);
            dispatch(setMainNews(data.articles));
            dispatch(setLoaderProgress(100))
        } catch (error) {
            console.log(error);
        }
    }
}

export const { setMainNews } = mainNewsSlice.actions;
export default mainNewsSlice.reducer;
