import axios from 'axios'
import React, { useEffect, useState } from 'react'
import NewsCard from '../../Components/NewsCard/NewsCard'
import { useSelector, useDispatch } from 'react-redux';
import { setMainNews, fetchNews } from '../../Store/MainNewsSlice'
import { setLoaderProgress } from '../../Store/LoaderValueSlice'
import './News.css'

function News() {
  const [category, setCategory] = useState('general');
  const Dispatch = useDispatch();
  const newsData = useSelector(state => state.mainNews);

  useEffect(() => {
    // const url = `https://newsapi.org/v2/top-headlines?apiKey=2af91b532974457caf3e61b21463a000&country=in&category=${category}`;
    // Dispatch(setLoaderProgress(30));
    // axios({ method: 'get', baseURL: url })
    //   .then(res => {
    //     Dispatch(setMainNews(res.data.articles));
    //     Dispatch(setLoaderProgress(100));
    //   })
    Dispatch(fetchNews(category))

  }, [category, Dispatch])

  return (
    <>
      <h1 className='mainHeading'>Top <span>{category} Headlines</span></h1>
      <nav id='newsNavbar'>
        <ul>
          <li onClick={() => setCategory('general')}>general</li>
          <li onClick={() => setCategory('entertainment')}>entertainment</li>
          <li onClick={() => setCategory('business')}>business</li>
          <li onClick={() => setCategory('health')}>health</li>
          <li onClick={() => setCategory('science')}>science</li>
          <li onClick={() => setCategory('sports')}>sports</li>
          <li onClick={() => setCategory('technology')}>technology</li>
        </ul>
      </nav>

      <div className="newsContainer">
        {newsData.map((data, ind) => <NewsCard key={ind} details={data} />)}
      </div>
    </>
  )
}

export default News
