import { useState } from 'react'
import '../Assets/Styles/About.css'
import '../Assets/Styles/Home.css'

function About() {
  useState(true)
  let optionDetail = [
    { bankvalue: 'pnb', bankname: 'Punjab National Bank' },
    { bankvalue: 'ab', bankname: 'Axis Bank' },
    { bankvalue: 'sbi', bankname: 'State Bank of India' },
  ]
  return (
    <div style={{ width: '300px' }}>

      <div className="bankDetailSelectContainer">
        <i className="fa-sharp fa-solid fa-building-columns bankDetailSelectIcon"></i>
        <p>Select Bank</p>
        <select name="bankname" id="selectBank" className='bankDetailFormSelect'>
          <option value="">Select Bank</option>
          <option value="axis_bank">Axis Bank</option>
          <option value="punjab_national_bank">Punjab National Bank</option>
        </select>
        <i className="fa-solid fa-caret-down bankDetailSelectDropDownIcon"></i>
        { <div className="bankDetailOptionContainer">
          {optionDetail.map((details) => {
            return (
              <div className='bankDetailOptionSelector'>{details.bankname}</div>
            );
          })}
        </div>}
      </div>
    </div>
  )
}

export default About
